import { ref, computed, watch } from 'vue';
import { useStore } from 'vuex';
export default {
  name: 'GlobalErrorHandler',
  setup: function setup() {
    var store = useStore();
    var show = ref(false);
    var timeout = 5000;
    var error = computed(function () {
      return store.state.error;
    });
    var errorMessage = computed(function () {
      if (!error.value) return '';
      if (error.value.response) {
        var status = error.value.response.status;
        var message = error.value.response.data.message;
        switch (status) {
          case 400:
            return '入力内容に誤りがあります';
          case 401:
            return '認証に失敗しました。再度ログインしてください';
          case 403:
            return 'この操作を実行する権限がありません';
          case 404:
            return 'リソースが見つかりません';
          case 500:
            return 'サーバーエラーが発生しました';
          default:
            return message || 'エラーが発生しました';
        }
      }
      return 'エラーが発生しました';
    });
    var errorType = computed(function () {
      if (!error.value) return 'error';
      if (error.value.response) {
        var status = error.value.response.status;
        switch (status) {
          case 401:
          case 403:
            return 'warning';
          default:
            return 'error';
        }
      }
      return 'error';
    });

    // エラーが発生したらスナックバーを表示
    var showError = function showError() {
      if (error.value) {
        show.value = true;
      }
    };

    // エラーの監視
    watch(error, showError);
    return {
      show: show,
      timeout: timeout,
      errorMessage: errorMessage,
      errorType: errorType
    };
  }
};
